import React , { Fragment ,useState } from 'react';
import {Button , Table} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';
import {Link,useNavigate} from 'react-router-dom'
import {v4 as uiid} from 'uiid';

function Create(){
    let Navigation = useNavigate();

    const handleSubmit =(e)=>{
        e.preventDefault();
        let uid = uiid();
        let uniqueId = uid.slice(0,8);
        ls.puch({id:uniqueId,name:e.name,age:e.age});
        Navigation('/');
    }

    const [name,setName] = useState('');
    const [age,setAge] = useState('');



    return(

        <div className="card" style={{ margin: '0 auto', border: '2px solid black', width: '300px' }}>
        <input type="text" placeholder="name" onChange={(e)=>e.target.value}/>
        <input type="text" placeholder="age" onChange={(e)=>e.target.value} />
        <Button  onClick={(e)=>handleSubmit(e)}>Submit</Button>
    </div>



    )
}
