import React , { Fragment ,useEffect } from 'react';
import {Button , Table} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';
import {Link,useNavigate} from 'react-router-dom'

function Home(){

    let Navigation = useNavigate();




    const HandleDelete = (id) => {



        let index = ls.findIndex((e) => e.id === id);

        ls.splice(index ,1);
        console.log(ls);
         Navigation('/');
    }



    return(
        <Fragment>



            <div className="container" style={{marginTop:"10rem"}}>
            <h1>User List</h1>

                <Table striped bordered hover size="sm" >
                    <thead>
                       <tr>
                        <td>Name</td>
                        <td>age</td>
                        <td>Button</td>
                       </tr>
                   </thead>
                   <tbody>
                    {
                        ls && ls.length > 0 ? ls.map((item) => { 
                            return(
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.age}</td>
                                    <td> <Link to={'/edit'} state={{ data: {name:item.name,age:item.age,id:item.id}}}><Button className="btn-success">Edit</Button></Link> &nbsp;
                                    <Button className="btn-danger" onClick={()=>HandleDelete(item.id)}>Delete</Button></td>
                                </tr>

                            )

                        }) : " NO data Available "
                    }

                   </tbody>


                </Table>
                <br></br>
                <br></br>
                <Link  to={'/add'}><Button size='lg'>Create</Button></Link>



            </div>
            </Fragment>


    )



}

export default Home;
