import React, { Fragment, useEffect } from 'react';
import { Button, Table } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';

function Home() {
    useEffect(() => {
        const handleDelete = (id) => {
            let index = ls.findIndex((e) => e.id === id);
            ls.splice(index, 1);
            console.log(ls);
        };

    }, [ls]); // Removed 'id' from dependency array because it's not defined here

    return (
        <Fragment>
            <div className="container" style={{ marginTop: "10rem" }}>
                <Table striped bordered hover size="sm" >
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Age</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {ls && ls.length > 0 ? ls.map((item, index) => (
                            <tr key={index}>
                                <td>{item.name}</td>
                                <td>{item.age}</td>
                                <td>
                                    <Button className="btn-success" onClick={() => alert(item.id)}>Edit</Button>
                                    <Button className="btn-secondary" onClick={() => handleDelete(item.id)}>Delete</Button>
                                </td>
                            </tr>
                        )) : <tr><td colSpan="3">NO data Available</td></tr>}
                    </tbody>
                </Table>
            </div>
        </Fragment>
    )
}

export default Home;
