import React , { Fragment ,useState,useEffect } from 'react';
import {Button , Table} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';
import {Link,useNavigate} from 'react-router-dom'

function Home(){

    let Navigation = useNavigate();


    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://my-app-db-33c96-default-rtdb.firebaseio.com/my_app.json');
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const fetchedData = await response.json();
                setData(fetchedData);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }
    console.log(data);

    const HandleDelete = (id) => {



        let index = ls.findIndex((e) => e.id === id);

        ls.splice(index ,1);
        console.log(ls);
         Navigation('/');
    }



    return(
        <Fragment>



            <div className="container" style={{marginTop:"10rem"}}>
            <h1>User List</h1>

                <Table striped bordered hover size="sm" >
                    <thead>
                       <tr>
                        <td>Name</td>
                        <td>age</td>
                        <td>Button</td>
                       </tr>
                   </thead>
                   <tbody>
                    {
                        data && Object.keys(data).length >0 ? Object.keys(data).map((key) => {
                            const item = data[key];
                            return(
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.age}</td>
                                    <td> <Link to={'/edit'} state={{ data: {name:item.name,age:item.age,id:item.id}}}><Button className="btn-success">Edit</Button></Link> &nbsp;
                                    <Button className="btn-danger" onClick={()=>HandleDelete(item.id)}>Delete</Button></td>
                                </tr>

                            )

                        }) : " NO data Available "
                    }

                   </tbody>


                </Table>
                <br></br>
                <br></br>
                <Link  to={'/add'}><Button size='lg'>Create</Button></Link>



            </div>
            </Fragment>


    )



}

export default Home;
