import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';
import { useNavigate } from 'react-router-dom';
import { v4 as uuid } from "uuid";

function Create() {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const navigate = useNavigate(); // Renamed Navigation to navigate as it's a convention to use camelCase for variable names

    const handleSubmit = (e) => {
        e.preventDefault();
        let uid = uuid(); // Changed uiid to uuid
        let uniqueId = uid.slice(0, 8);
        ls.push({ id: uniqueId, name: name, age: age }); // Corrected typo in push method
        navigate('/'); // Changed Navigation to navigate
    }

    return (
        <div className="card" style={{ margin: '10rem auto', width: '300px' }}>
            <input type="text" placeholder="name" onChange={(e) => setName(e.target.value)} />
            <input type="text" placeholder="age" onChange={(e) => setAge(e.target.value)} />
            <Button onClick={(e) => handleSubmit(e)}>Submit</Button>
        </div>
    )
}

export default Create;
