import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';
import { useNavigate } from 'react-router-dom';
import { v4 as uuid } from "uuid";
import { useLocation } from 'react-router-dom';

function Edit(props) {
    const location = useLocation();

    console.log(props.data);
    console.log(location);

    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [id, setId] = useState('');
    const navigate = useNavigate(); // Renamed Navigation to navigate as it's a convention to use camelCase for variable names

    const handleSubmit = (e) => {
        e.preventDefault();
        let uid = uuid(); // Changed uiid to uuid
        let uniqueId = uid.slice(0, 8);
        ls.push({ id: uniqueId, name: name, age: age }); // Corrected typo in push method
        navigate('/'); // Changed Navigation to navigate
    }

    return (
        <div className="card" style={{ margin: '10rem auto', width: '300px',padding:'2rem' }}>
            <h2>Edit User</h2><br></br>
            <input type="text" placeholder="name" value={name} required onChange={(e) => setName(e.target.value)} /><br></br>
            <input type="text" placeholder="age" value={age} required  onChange={(e) => setAge(e.target.value)} /><br></br>
            <Button onClick={(e) => handleSubmit(e)}>Submit</Button>
        </div>
    )
}

export default Edit;
