import React, { useState ,useEffect } from 'react';
import { Button } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';
import { useNavigate } from 'react-router-dom';
import { v4 as uuid } from "uuid";
import { useLocation } from 'react-router-dom';

function Edit() {
    const location = useLocation();


    console.log(location.state.data.name);

    const [Datakey,setDatakey] = useState();

    const [Newdata, setNewdata] = useState({
        name:'',
        age:'',
        id:'',
    });

    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [id, setId] = useState('');
    const navigate = useNavigate(); 

    useEffect(() => {

        setNewdata({ ...Newdata, name: location.state.data.name });
        setNewdata({ ...Newdata, name: location.state.data.age });
        setNewdata({ ...Newdata, name: location.state.data.id });
        setDatakey(location.state.data.datakey);



      }, []);
      const updateData = async (Datakey, Newdata) => {
        try {
            const response = await fetch(`https://my-app-db-33c96-default-rtdb.firebaseio.com/my_app/${Datakey}.json`, {
                method: 'PATCH', // Use PATCH or PUT for updating data
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(Newdata) // Include the updated data in the request body
            });

            if (response.ok) {
                console.log(`Data with key ${Datakey} updated successfully.`);
                // Refetch data after successful update
                fetchData();
            } else {
                throw new Error('Failed to update data.');
            }
        } catch (error) {
            console.error('Error:', error.message);
        }
        Navigation('/');
    };


    const handleSubmit = (e) => {
        let index = ls.findIndex((e) => e.id === id);
        e.preventDefault();
        let emp = ls[index];
       emp.name = name;
       emp.age = age;
        navigate('/'); 
    }

    return (
        <div className="card" style={{ margin: '10rem auto', width: '300px',padding:'2rem' }}>
            <h2>Edit User</h2><br></br>
            <input type="text" placeholder="name" value={Newdata.name} required onChange={(e) => setNewdata({ ...Newdata, name: e.target.value })} /><br></br>
            <input type="text" placeholder="age" value={Newdata.age} required  onChange={(e) => setNewdata({...Newdata, age: e.target.value })} /><br></br>
            <Button onClick={(e) => handleSubmit(e)}>Update</Button>
        </div>
    )
}

export default Edit;
