import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import { Link, useNavigate } from 'react-router-dom';
import { v4 as uuid } from "uuid";

function Create() {
    //const navigate = useNavigate();
    const [User, setUser] = useState({
        name:'',
        age:''
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        let uid = uuid(); 
        let uniqueId = uid.slice(0, 8);

        var req = await fetch('https://my-app-db-33c96-default-rtdb.firebaseio.com/my_app.json', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },

            body: JSON.stringify({ id: uniqueId, name: User.name, age: User.age })
        });

        //navigate('/');
    }

    return (
        <div className="card" style={{ margin: '10rem auto', width: '300px', padding: '2rem' }}>
            <h2>Create User</h2><br></br>
            <form method='post'>
                <input type="text" placeholder="name" required onChange={(e) => setUser({ ...User, name: e.target.value })} /><br></br>
                <input type="text" placeholder="age" required onChange={(e) => setUser({ ...User, age: e.target.value })} /><br></br>
                <Button onClick={(e) => handleSubmit(e)}>Submit</Button>
                <Link to={'/'}><Button>Back To User List...</Button></Link>
            </form>
        </div>
    )
}

export default Create;
