import React , { Fragment } from 'react';
import {Button , Table} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import ls from './list';

function Home(){

    const HandleDelete = (id) =>{

        index = ls.map((e)=> {if(e.id == id){
            return e.id+1;
        }})

        console.log(index);

    }

    return(
        <Fragment>

            <div className="container" style={{marginTop:"10rem"}}>

                <Table striped bordered hover size="sm" >
                    <thead>
                       <tr>
                        <td>Name</td>
                        <td>age</td>
                        <td>Button</td>
                       </tr>
                   </thead>
                   <tbody>
                    {
                        ls && ls.length > 0 ? ls.map((item) => { 
                            return(
                                <tr>
                                    <td>{item.name}</td>
                                    <td>{item.age}</td>
                                    <td><Button className="btn-success" onClick={()=>alert(item.id)}>Edit</Button> &nbsp;
                                    <Button className="btn-secondary" onClick={()=>HandleDelete(item.id)}>Delete</Button></td>
                                </tr>

                            )

                        }) : " NO data Available "
                    }
                   </tbody>


                </Table>




            </div>
            </Fragment>


    )



}

export default Home;
